describe('Ecosia.org Demo', function() {

  before(browser => browser.url('https://www.ecosia.org/'));

  test('Demo test ecosia.org', function (browser) {
    browser
      .waitForElementVisible('body')
      .assert.titleContains('Ecosia')
      .assert.visible('input[type=search]')
      .setValue('input[type=search]', 'nightwatch')
      .assert.visible('button[type=submit]')
      // .click('button[type=submit]') better to the use xpath
      .click('#__layout > div > section > div.content > form > div.buttons-wrapper > button.button.button-submit.icon-button.icon-button--elevation-0.icon-button--size-m.icon-button--variant-bare')
      .assert.containsText('.mainline-results', 'Nightwatch.js')
  });

  after(browser => browser.end());
});
